import React from "react";
export default function Cervezas(props){
    return(
        <article>
            <div className="imagen_producto">
                <img src={ruta_imagen} alt={nombre_producto} />
            </div>
            <div className="detalles_producto">
                <h3>{nombre_producto}</h3>
                <span>{precio}</span>
                <div>
                    <button> + </button>
                    <span>{cantidad}</span>
                    <button> - </button>
                </div>
                <button> Agregar </button>
            </div>
        </article>
    );
}
