import { Link } from "react-router-dom";
import "../css/iniciar_sesion_styles.css";


export default function Iniciosesion(){
    const [redigir, setRedirigir] = useState(false);
  const manejarEnvio=()=>{
    setRedirigir(true);
  }
  if(redigir){
    return(
      <BrowserRouter>
        <div className='contenedor_pagina'>
          <Encabezado />
        </div>
        <Routes>
          <Route path='/PaginaPrincipal' element={<PPrincipal />} />
          <Route path='/PaginaProducto' element={<Productos/>} />
          <Route path='/Ofertas' element={<Ofertas />} />
          <Route path='/PaginaSNosotros' element={<PSNosotros />} />
          <Route path='/Login' element={<Iniciosesion />} />
          <Route path='/Registro' element={<Registro />} />
        </Routes>
      </BrowserRouter>
    )
  }
    return(
        <section className="cuerpo">

            <form className="formulario" action="">
                <h2> LOGIN </h2>
                <label htmlFor=""> Usuario </label>
                <input type="text" />
                <label htmlFor=""> Contraseña </label>
                <input type="password" />
                <p> ¿ No tienes una cuenta ? <Link to="/Resgitro"><span>Resgistrate</span></Link> </p>
                <button className="ingresar_login"> Ingresar </button>
            </form>
            
        </section>
    );
}
