import React,{useState} from "react";
export default function Cervezas(props){
    const [cantidad, setCantidad] = useState(0);

  const handleIncrement = () => {
    setCantidad(cantidad + 1);
  };

  const handleDecrement = () => {
    if (cantidad > 0) {
      setCantidad(cantidad - 1);
    }
  };
    return(
        <article>
            <div className="imagen_producto">
                <img src={props.ruta_imagen} alt={props.nombre_producto} />
            </div>
            <div className="detalles_producto">
                <h3>{props.nombre_producto}</h3>
                <span>{props.precio}</span>
                <div>
                    <button onClick={handleDecrement}> - </button><span>{cantidad}</span><button onClick={handleIncrement}> + </button>
                </div>
                <button> Agregar </button>
            </div>
        </article>
    );
}
